import React, { Component } from 'react';
import { Link } from 'react-router-dom';

let specs = ['Уп. материалы', 'Металлоконтейнеры']

class Filters extends Component {
  constructor() {
    super();
        this.state = {
          fname: '', 
          fgroup: '', 
          fcountry: '', 
          fspec: []
        };
  
        this.onClick = this.onClick.bind(this)

        this.onChange = this.onChange.bind(this)
    }

    onClick(e){
      e.preventDefault();

      let fname = document.getElementById('name_select').value
      
      let fgroup = ""
      if (document.getElementById('gridRadios1').checked) fgroup = 'Поставщик'
      if (document.getElementById('gridRadios2').checked) fgroup = 'Клиент'
      if (document.getElementById('gridRadios3').checked) fgroup = 'Клиент, поставщик'
      
      let fcountry = document.getElementById('country_select').value

      let fspec = []
      for (let i=0; i<specs.length;i++) {
        if  (document.getElementById(i).checked) fspec.push(document.getElementById(i).value)
      }
      
      this.setState({
        fname: fname, 
        fgroup: fgroup, 
        fcountry: fcountry, 
        fspec: fspec
      })

      const filterData = {
        fname: this.state.fname, 
        fgroup: this.state.fgroup, 
        fcountry: this.state.fcountry, 
        fspec: this.state.fspec,
      }

      this.props.handler(filterData)
    }
    
    onChange(e) {
      console.log("1")
    }


  render() {
    return( 
    <div className="filter">
        <div>
          <h6>Название</h6>
        </div>
          <select className="form-select" id="name_select">
            <option disabled>Выберите название</option>
            <option selected></option>
            {this.props.data.map((item, i)=>{
        return (
            <option value={item.name}>{item.name}</option>
        )})}
        </select>
       


        <div>
            <h6 className="col-form-label col-sm-2 pt-0">Группа</h6>
            </div>
        <fieldset className="row mb-3">
    <div className="col-sm-10">
      <div className="form-check">
        <input className="form-check-input" type="radio" name="gridRadios" id="gridRadios1" value='Поставщик' onChange={this.onChange}/>
        <label className="form-check-label" for="gridRadios1">
          Поставщик
        </label>
      </div>
      <div className="form-check">
        <input className="form-check-input" type="radio" name="gridRadios" id="gridRadios2" value='Клиент' onChange={this.onChange}/>
        <label className="form-check-label" for="gridRadios2">
          Клиент
        </label>
      </div>
      <div className="form-check">
        <input className="form-check-input" type="radio" name="gridRadios" id="gridRadios3" value='Клиент, поставщик' onChange={this.onChange}/>
        <label className="form-check-label" for="gridRadios3">
          Клиент, поставщик
        </label>
      </div>
    </div>
  </fieldset>


  <div>
          <h6>Страна</h6>
        </div>
          <select className="form-select" id="country_select">
            <option>Россия</option>
        </select>


        <div>
          <h6>Специализация</h6>
        </div>
        <div className="form-check">
            
        {specs.map((item, i)=>{
        return (
          <div className="form-check">
          <input className="form-check-input" type="checkbox" value={item} onChange={this.onChange} id={i}/>
          <label className="form-check-label" for="gridCheck1">
            {item}
          </label>
          </div>
        )})}

      </div>
      <button type="button" className="btn btn-outline-dark" onClick={this.onClick}>Применить</button>
    </div>
    )
  }
}

export default Filters;
