import React, { Component } from 'react';
import { Link } from 'react-router-dom';

let specs = ['Уп. материалы', 'Металлоконтейнеры']

class Filters extends Component {
  constructor() {
    super();
        this.state = {
          fname: '', 
          fgroup: '', 
          fcountry: '', 
          fspec: ''
        };
  
        this.onClick = this.onClick.bind(this)

        this.onChange = this.onChange.bind(this)
    }

    onClick(e){
      e.preventDefault();
  
      const filterData = {
        fname: this.state.fname, 
        fgroup: this.state.fgroup, 
        fcountry: this.state.fcountry, 
        fspec: this.state.fspec,
      }
    }
    
    onChange(e) {
      this.setState({[e.target.name]: e.target.value});
    }


  render() {
    return( 
    <div className="filter">
        <div>
          <h6>Название</h6>
        </div>
          <select class="form-select" aria-label="Default select example">
            <option disabled>Выберите название</option>
            {this.props.data.map((item, i)=>{
        return (
            <option>{item.name}</option>
        )})}
        </select>
       


        <div>
            <h6 class="col-form-label col-sm-2 pt-0">Группа</h6>
            </div>
        <fieldset class="row mb-3">
    <div class="col-sm-10">
      <div class="form-check">
        <input class="form-check-input" type="radio" name="gridRadios" id="gridRadios1" value={this.state.fgroup} onChange={this.onChange} checked/>
        <label class="form-check-label" for="gridRadios1">
          Поставщик
        </label>
      </div>
      <div class="form-check">
        <input class="form-check-input" type="radio" name="gridRadios" id="gridRadios2" value={this.state.fgroup} onChange={this.onChange}/>
        <label class="form-check-label" for="gridRadios2">
          Клиент
        </label>
      </div>
      <div class="form-check">
        <input class="form-check-input" type="radio" name="gridRadios" id="gridRadios3" value={this.state.fgroup} onChange={this.onChange}/>
        <label class="form-check-label" for="gridRadios3">
          Клиент, поставщик
        </label>
      </div>
    </div>
  </fieldset>


  <div>
          <h6>Страна</h6>
        </div>
          <select class="form-select" aria-label="Default select example" value={this.state.fcountry} onChange={this.onChange}>
            <option>Россия</option>
        </select>


        <div>
          <h6>Специализация</h6>
        </div>
        <div class="form-check">
            
        {specs.map((item, i)=>{
        return (
          <div class="form-check">
          <input class="form-check-input" type="checkbox" value={this.state.fcountry} onChange={this.onChange}/>
          <label class="form-check-label" for="gridCheck1">
            {item}
          </label>
          </div>
        )})}

      </div>
      <button type="button" class="btn btn-outline-dark" onClick={this.onClick}>Применить</button>
    </div>
    )
  }
}

export default Filters;
